import React, { Component } from "react"
import { connect } from "react-redux"
import Question from "./Question"

class Dashboard extends Component {
  unanswered = () => {
    const container_list = document.getElementById('container_list_list')
    container_list.classNameList.add("right-panel-active")
  }
  answered = () => {
    const container_list = document.getElementById('container_list_list')
    container_list.classNameList.remove("right-panel-active")
  }

  render() {
    return (
      <div className="container_list" id="container_list">
    <div className="form-container_list sign-up-container_list">
        <form>
            <h1>Create Account</h1>
            <input type="text" placeholder="Username" name="username" />
            <input type="email" placeholder="Email" name="email" />
            <input type="password" placeholder="Password" name="password" />
            <input type="password" placeholder="Confirm Password" name="confirmation" />
            <button>Sign Up</button>
        </form>
    </div>
    <div className="form-container_list sign-in-container_list">
        <form>
            <h1>Sign in</h1>
            <input type="text" placeholder="Username" name="username" />
            <input type="password" placeholder="Password" name="password" />
            <button>Sign In</button>
        </form>
    </div>
    <div className="overlay-container_list">
        <div className="overlay">
            <div className="overlay-panel overlay-left">
                <h1>Welcome Back!</h1>
                <p>Please login with your personal info</p>
                <button className="ghost" id="signIn">Sign In</button>
            </div>
            <div className="overlay-panel overlay-right">
                <h1>Don't have an account?</h1>
                <p>Register here</p>
                <button className="ghost" id="signUp">Sign Up</button>
            </div>
        </div>
    </div>
</div>
    )
  }
}

function mapStateToProps({ questions }) {
  return {
    questionIds: Object.keys(questions).sort(
      (a, b) => questions[b].timestamp - questions[a].timestamp
    ),
  }
}

export default connect(mapStateToProps)(Dashboard)
