import React, { Component } from 'react'
import { connect } from "react-redux"
import LeaderBoardUser from "./LeaderBoardUser"

class LeaderBoard extends Component {
	render() {
		return (
			<div className='leaderboard'>
				{this.props.users.map((id) => (
		            <li key={id} className="question">
		              <LeaderBoardUser id={id} />
		            </li>
		          ))}
			</div>
		)
	}
}

function mapStateToProps({ users }) {
	const sortable = Object.fromEntries(
    Object.entries(users).sort(([,a],[,b]) => (Object.values(b.answers).length) - (Object.values(b.answers).length)))

	console.log(sortable)

	var sorted_categories = users.sort(function (one, other) {
   return one.elements.length - other.elements.length;
})
	console.log(sorted_categories)
	return {
		users: Object.values(users).map((user) => user.id)
	}
}

export default connect(mapStateToProps)(LeaderBoard)
