import React, { Component } from "react"
import logo from "../loginicon.png"
import { connect } from "react-redux"
import { handleSetAuthedUser } from "../actions/authedUser"
import { Redirect } from "react-router-dom"

export const fakeAuth = {
	isAuthenticated: false,
	authenticate() {
		this.isAuthenticated = true
	},
	signout(cb) {
		this.isAuthenticated = false
	},
}

class Login extends Component {
	state = {
		selectedUser: "none",
		toMain: false,
	}

	handleChange = (e) => {
		this.setState({
			selectedUser: e.target.value,
		})
	}
	handleSubmit = (formSubmitEvent) => {
		formSubmitEvent.preventDefault()
		const { selectedUser } = this.state
		const { dispatch } = this.props

		dispatch(handleSetAuthedUser(selectedUser))
		fakeAuth.authenticate()
		this.setState(() => ({
			toMain: true,
		}))
	}
	render() {
		const { from } = this.props.location.state || {
			from: { pathname: "/react-game" },
		}
		const { selectedUser, toMain } = this.state
		if (toMain === true) {
			return <Redirect to={from} />
		}
		const { users } = this.props
		let path = ""
		if (from.pathname === "/react-game") {
			path = "Home Page"
		} else if (from.pathname === "/react-game/add") {
			path = "New Question Page"
		} else if (from.pathname === "/react-game/leaderboard") {
			path = "Leader Board Page"
		}
		return (
			<div className="container_body">
				<div className="login-greeting">
					<span>Welcome to the Would You Rather APP!</span>
					<p>Please sign in to view {path}</p>
				</div>
				<div className="login_body">
					<img src={logo} alt="logo" className="logo" />
					<p>Sign in</p>
					<form onSubmit={this.handleSubmit}>
						<select
							className="select-login"
							onChange={this.handleChange}
							value={selectedUser}
						>
							<option value="none" disabled>
								Select User
							</option>
							{users.map((user) => (
								<option key={user.id} value={user.id}>
									{user.name}
								</option>
							))}
						</select>
						<br />
						<button
							className="sign-btn"
							disabled={selectedUser === "none"}
						>
							Sign in
						</button>
					</form>
				</div>
			</div>
		)
	}
}

function mapStateToProps({ authedUser, users }) {
	return {
		authedUser,
		users: Object.values(users).map((user) => user),
	}
}

export default connect(mapStateToProps)(Login)
