import { saveQuestionAnswer } from '../utils/api'
import { showLoading, hideLoading } from 'react-redux-loading'

export const RECEIVE_QUESTIONS = 'RECEIVE_QUESTIONS'
export const ADD_QUESTION_ANSWER = 'ADD_QUESTION_ANSWER'

export function receiveQuestions (questions) {
	return {
		type: RECEIVE_QUESTIONS,
		questions
	}
}

function addQuestion (question) {
  return {
    type: ADD_QUESTION_ANSWER,
    question,
  }
}

export function handleAddQuestion (authedUser, qid, answer) {
  return (dispatch, getState) => {
    const { authedUser } = getState()
    dispatch(showLoading())

    return saveQuestion({
      qid,
      author: authedUser,
      answer
    })
      .then((question) => dispatch(addQuestion(question)))
      .then(() => dispatch(hideLoading()))
  }
}

function QuestionAswer ({ id, authedUser, hasLiked }) {
  return {
    type: ADD_QUESTION_ANSWER,
    id,
    authedUser,
    hasLiked
  }
}

export function handleAddQuestionAswer (info) {
  return (dispatch) => {
    dispatch(handleAddQuestionAswer(info))

    return saveQuestionAnswer(info)
      .catch((e) => {
        console.warn('Error in handleToggleTweet: ', e)
        dispatch(QuestionAswer(info))
        alert('The was an error liking the tweet. Try again.')
      })
  }
}
