import React, { Component } from "react";
import { connect } from "react-redux";

class Result extends Component {
	render() {
		const { avatarURL, name } = this.props.author;
		const {
			total_answers,
			authedUser_answer,
			this_answer_count,
			progress,
		} = this.props;
		// if (authedUser_answer === 'optionOne') {
		// 	const optionOne = document.getElementById("one")
  //   		optionOne.classList.add("selected")
		// } else if (authedUser_answer === 'optionTwo') {
		// 	const optionOne = document.getElementById("two")
  //   		optionOne.classList.add("selected")
		// }
		return (
			<div className="container_body">
				<div className="user_asks">
					<span>Asked by {name}</span>
				</div>
				<div className="question-inner">
					<img
						src={avatarURL}
						alt={`Avatar of ${name}`}
						className="avatar"
					/>
					<div className="result-info">
						<div>
							<span>Results:</span>
						</div>
						<div className = 'option one'>
							<p>Would you ...</p>
							<div className="progress_bar"> 
						        <div style={{width: `${progress}%`}} className="progress">{progress}%</div> 
						    </div> 
							<p className='answer_count'>
								{this_answer_count} out of {total_answers} votes
							</p>
						</div>
						<div className='option two'>
							<p>Would you ...</p>
							<div className="progress_bar"> 
						        <div style={{width: `${progress}%`}} className="progress">{progress}%</div> 
						    </div> 
							<p className='answer_count'>
								{this_answer_count} out of {total_answers} votes
							</p>
						</div>
					</div>
				</div>
			</div>
		);
	}
}

function mapStateToProsp({ authedUser, questions, users }, props) {
	const { id } = props.match.params;
	const question = questions[id];
	const author = users[question.author];
	const total_answers =
		Object.keys(question.optionTwo.votes).length +
		Object.keys(question.optionOne.votes).length;

	const authedUser_answer = users[authedUser].answers[id];
	console.log(authedUser_answer)

	const this_answer_count = Object.keys(question[authedUser_answer].votes).length;

	return {

		total_answers,
		authedUser_answer,
		this_answer_count,
		author,
		progress: ((this_answer_count * 100) / total_answers).toFixed(1),
	};
}

export default connect(mapStateToProsp)(Result);
