import { RECEIVE_USERS } from '../actions/users'
import { ADD_QUESTION_ANSWER_USER } from '../actions/questions'

export default function users (state = {}, action) {
	switch(action.type) {
		case RECEIVE_USERS :
			return {
				...state,
				...action.users
			}
		case ADD_QUESTION_ANSWER_USER :
			//const { answer } = action
			console.log('HERE:', state[action.authedUser])
			return {
				...state,
			        [action.authedUser]: {
			          ...state[action.authedUser],
			          answers: state[action.authedUser].answers.concat([action.qid])
			        }
			}
		default :
			return state
	}
}
