import React, { Component } from "react"
import { connect } from "react-redux"
import Question from "./Question"

class Dashboard extends Component {
  unanswered = () => {
    const container = document.getElementById('container_list')
    container.classList.add("right-panel-active")
  }
  answered = () => {
    const container = document.getElementById('container_list')
    container.classList.remove("right-panel-active")
  }

  render() {
    return (
      <div>
        <div className="container_list" id="container_list">
    <div className="form-container_list sign-up-container_list questions_list">

            <h1>Unanswered</h1>
    </div>
    <div className="form-container_list sign-in-container_list questions_list">
            <h1>Answered</h1>
    </div>
    <div className="overlay-container_list">
        <div className="overlay">
            <div className="overlay-panel overlay-left">
                <h1>Don't feel to answer?</h1>
                <p>Wanna see answered questions?</p>
                <button 
                className="ghost" 
                id="signIn"
                onClick={this.answered}
                >
                View Answered</button>
            </div>
            <div className="overlay-panel overlay-right">
                <h1>Want more questions?</h1>
                <p>Check out all questions</p>
                <button className="ghost"
                 id="signUp"
                 onClick={this.unanswered}
                 >
                View</button>
            </div>
        </div>
    </div>
</div>


        <ul className="dashboard-list">
          {this.props.questionIds.map((id) => (
            <li key={id} className="question">
              <Question id={id} />
            </li>
          ))}
        </ul>
      </div>
    )
  }
}

function mapStateToProps({ questions }) {
  return {
    questionIds: Object.keys(questions).sort(
      (a, b) => questions[b].timestamp - questions[a].timestamp
    ),
  }
}

export default connect(mapStateToProps)(Dashboard)
