import { RECEIVE_QUESTIONS, ADD_QUESTION_ANSWER } from '../actions/questions'

export default function questions(state = {}, action) {
    switch (action.type) {
        case RECEIVE_QUESTIONS:
        	console.log(state)
            return {
                ...state,
                ...action.questions
            }
        case ADD_QUESTION_ANSWER:
        	return {
        		...state,
                [action.qid]: {
                    ...state[action.qid],
                    likes: action.hasLiked === true ?
                        state[action.id].likes.filter((uid) => uid !== action.authedUser) : state[action.id].likes.concat([action.authedUser])
                }
        	}
        default:
            return state
    }
}
