function mapStateToProsp({
  authedUser,
  questions,
  users
}, props) {
  const {
    id
  } = props.match.params;
  const question = questions[id];
  const author = users[question.author];
  const total_answers = Object.keys(question.optionTwo.votes).length + Object.keys(question.optionOne.votes).length;
  const authedUser_answer = users[authedUser].answers[id];
  const selected_option = question[authedUser_answer];
  let another_option = '';

  if (selected_option.text === question.optionOne.text) {
    another_option = question.optionTwo;
  } else {
    another_option = question.optionOne;
  }

  const selected_option_count = Object.keys(selected_option.votes).length;
  const another_option_count = Object.keys(another_option.votes).length;
  return {
    author,
    total_answers,
    authedUser_answer,
    selected_option_count,
    another_option_count,
    selected_option_progress: (selected_option_count * 100 / total_answers).toFixed(1),
    another_option_progress: (another_option_count * 100 / total_answers).toFixed(1),
    selected_option,
    another_option
  };
}

export default connect(mapStateToProsp)(Result);
